  };

  return (
    <div>
      <button
        onClick={() => {
          getCard();
        }}
      >
        Nueva Cartilla
      </button>
      <div className="clear"></div>
      <div>
        <div className="card">
          <div className="headers">
            <div>
              <span>B</span>
            </div>
            <div>
              <span>I</span>
            </div>
            <div>
              <span>N</span>
            </div>
            <div>
              <span>G</span>
            </div>
            <div>
              <span>O</span>
            </div>
          </div>
          <div className="card-body">
            {cardNumbers &&
              Object.values(cardNumbers).map((colum: any, colIdx: number) => {
                return (
                  <div className={`column-${colum}`}>
                    {colum &&
                      colum.map((number: number, idx: number) => {
                        return (
                          <div
                            className={`number col-1${colIdx + 1}${idx + 1}`}
                          >
                            <span>{number}</span>
                          </div>
                        );
                      })}
                  </div>
                );
              })}
          </div>

          {/* <div className="column 1">
            {cardNumbers}
          </div>
          <div className="column 2"></div>
          <div className="column 3"></div>
          <div className="column 4"></div>
          <div className="column 5"></div> */}
        </div>
        <div className="temp"></div>
      </div>
    </div>
  );
};

export default Home;
