import { useEffect, useState } from "react";
import "./Home.css";
import bingoService from "../services/Bingo";

const Home = () => {
  const [cardNumbers, setCardsNumbers] = useState({});
  const getNewNumbers = async () => {
    await bingoService.getNewNumbers();
  };
  const getCard = async () => {
    const { data } = await bingoService.getNewCard();
    debugger;
    setCardsNumbers(data.data.bingoOrdered);
  };

  useEffect(() => {}, [0]);

  function contarTachado() {
    let totalJugador = document.querySelectorAll(
      ".cartonJugador .tachado"
    ).length;
    let totalCpu = document.querySelectorAll(".cartonCpu .tachado").length;

    if (totalJugador == 15) alert("FELICIDADES CARTON JUGADOR COMPLETADO");
    if (totalCpu == 15) alert("FELICIDADES CARTON CPU COMPLETADO");

    console.log(totalJugador + " - " + totalCpu);
  }

  const sacarBola = () => {
    // let bola = Math.floor(Math.random() * rango.length);
    // bola = rango[bola];
    // _.pull(rango, bola);
    // console.log(rango);
    // let divbola: any = document.querySelector("#bola");
    // divbola.textContent = bola;
    // let numero = document.querySelectorAll(`.n${bola}`);
    // for (var i = 0; i < numero.length; ++i) {
    //   numero[i].classList.add("tachado");
    // }
  };

  return (
    <div className="game">
      <div className="cardPlayer">
        <button
          onClick={() => {
            getCard();
          }}
        >
          Nueva Cartilla
        </button>
        <div className="clear"></div>
        {Object.values(cardNumbers).length > 0 && (
          <div className="card">
            <div className="headers">
              <div>
                <span>B</span>
              </div>
              <div>
                <span>I</span>
              </div>
              <div>
                <span>N</span>
              </div>
              <div>
                <span>G</span>
              </div>
              <div>
                <span>O</span>
              </div>
            </div>
            {Object.values(cardNumbers).map((colum: any, colIdx: number) => {
              return (
                <div className={`column ${colIdx + 1}`}>
                  {colum &&
                    colum.map((number: number, idx: number) => {
                      return (
                        <div className={`number col-${colIdx + 1}${idx + 1}`}>
                          <span>{number !== 0 ? number : "FREE"}</span>
                        </div>
                      );
                    })}
                </div>
              );
            })}
          </div>
        )}
      </div>

      <div className="bingo">
        <div className="bola">43</div>
        <button onClick={getNewNumbers}>Empezar el juego</button>
      </div>
    </div>
  );
};

export default Home;
