import { useEffect, useState } from "react";
import "./Home.css";
import bingoService from "../services/Bingo";
import Bingo from "../components/Bingo";

const Home = () => {
  const [cardNumbers, setCardsNumbers] = useState<{}>({});
  const [cardId, setCardId] = useState<string>("");
  const [gameResult, setGameResult] = useState<number[]>([]);
  const [bolaSelected, setBolaSelected] = useState(0);

  const getCard = async () => {
    const { data } = await bingoService.getNewCard();
    setGameResult([]);
    setBolaSelected(0);
    debugger;
    setCardId(data.data.bingo_id);
    setCardsNumbers(data.data.bingoOrdered);
  };

  const sleep = (ms: number) => new Promise((r) => setTimeout(r, ms));

  const startGame = async () => {
    if (cardId) {
      const { data } = await bingoService.startGame(cardId);
      getBola(data.data);
    }
  };

  useEffect(() => {}, [0]);

  const getBola = async (bingoResult: number[]) => {
    const newGameRes: number[] = [];

    for (const element of bingoResult) {
      setBolaSelected(element);
      newGameRes.push(element);
      setGameResult([...newGameRes]);
      document.querySelector(".bola-" + element)?.classList.add("focus");
      await sleep(1000);
    }
  };

  useEffect(() => {}, [0]);

  return (
    <div className="game">
      <div className="cardPlayer">
        <button onClick={getCard}>Nueva Cartilla</button>
        <div className="clear"></div>
        {Object.values(cardNumbers).length > 0 && (
          <div className="card">
            <div className="headers">
              <div>
                <span>B</span>
              </div>
              <div>
                <span>I</span>
              </div>
              <div>
                <span>N</span>
              </div>
              <div>
                <span>G</span>
              </div>
              <div>
                <span>O</span>
              </div>
            </div>
            {Object.values(cardNumbers).map((colum: any, colIdx: number) => {
              return (
                <div className={`column ${colIdx + 1}`} key="{colIdx}">
                  {colum &&
                    colum.map((number: number, idx: number) => {
                      const focus = gameResult.find(
                        (winnerNumber: number) => winnerNumber === number
                      );
                      return (
                        <div
                          className={`number bola-${number} focus`}
                          key={number}
                        >
                          <span>{number !== 0 ? number : "FREE"}</span>
                          {focus && <span className="mark">X</span>}
                        </div>
                      );
                    })}
                </div>
              );
            })}
          </div>
        )}
      </div>

      <Bingo
        cardNumbers={Object.values(cardNumbers).length}
        startGame={startGame}
        bolaSelected={bolaSelected}
        gameResult={gameResult}
      />
    </div>
  );
};

export default Home;
