import { useEffect, useState } from "react";
import "./Home.css";
import bingoService from "../services/Bingo";

const Home = () => {
  const [cardNumbers, setCardsNumbers] = useState({});
  const [gameResult, setGameResult] = useState<number[] | null>([]);
  const [bolaSelected, setBolaSelected] = useState(0);

  const sleep = (ms: number) => new Promise((r) => setTimeout(r, ms));

  const startGame = async () => {
    const { data } = await bingoService.startGame();
    getBola(data.data);
  };

  const getCard = async () => {
    const { data } = await bingoService.getNewCard();
    setCardsNumbers(data.data.bingoOrdered);
  };

  useEffect(() => {}, [0]);

  const getBola = async (bingoResult: number[]) => {
    for (const element of bingoResult) {
      setBolaSelected(element);
      const newGameRes: number[] = [];
      newGameRes.push(element);
      setGameResult(newGameRes);
      await sleep(1000);
    }
  };

  return (
    <div className="game">
      <div className="cardPlayer">
        <button onClick={getCard}>Nueva Cartilla</button>
        <div className="clear"></div>
        {Object.values(cardNumbers).length > 0 && (
          <div className="card">
            <div className="headers">
              <div>
                <span>B</span>
              </div>
              <div>
                <span>I</span>
              </div>
              <div>
                <span>N</span>
              </div>
              <div>
                <span>G</span>
              </div>
              <div>
                <span>O</span>
              </div>
            </div>
            {Object.values(cardNumbers).map((colum: any, colIdx: number) => {
              return (
                <div className={`column ${colIdx + 1}`} key="{colIdx}">
                  {colum &&
                    colum.map((number: number, idx: number) => {
                      return (
                        <div
                          className={`number col-${colIdx + 1}${idx + 1}`}
                          key={number}
                        >
                          <span>{number !== 0 ? number : "FREE"}</span>
                        </div>
                      );
                    })}
                </div>
              );
            })}
          </div>
        )}
      </div>

      <div className="bingo">
        <div className="bola">{bolaSelected}</div>
        <button
          onClick={startGame}
          disabled={Object.values(cardNumbers).length === 0}
        >
          Empezar el juego
        </button>
        <div>
          Resultados:
          {gameResult?.map((numberWinner) => (
            <span>{numberWinner}</span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
