import { useEffect, useState } from "react";
import "./Home.css";
import bingoService from "../services/Bingo";

const Home = () => {
  const [cardNumbers, setCardsNumbers] = useState({});
  const getNewNumbers = async () => {
    await bingoService.getNewNumbers();
  };
  const getCard = async () => {
    const { data } = await bingoService.getNewCard();
    debugger;
    setCardsNumbers(data.data.bingoOrdered);
  };

  // setTimeout;
  useEffect(() => {}, [0]);

  //   var rango = _.range(1, 90);
  //   rango = _.shuffle(rango);
